import { useState, useEffect, useCallback } from "react";
import { useNavigate, Link, NavLink } from "react-router-dom";
import axios from "axios";
import "../styles/Dashboard.css";
import { FaUserEdit, FaTrash } from 'react-icons/fa';
import Sidebar from '../components/Sidebar';
import Topbar from '../components/Topbar';
import '../styles/global.css';

function Dashboard({ user, setUser }) {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [stats, setStats] = useState({
    totalTasks: 0,
    overdueTasks: 0,
    todayTasks: 0,
    completedTasks: 0
  });
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications] = useState([
    {
      id: 1,
      message: "New task assigned: Complete project documentation",
      time: "2 hours ago",
      photo: "https://ui-avatars.com/api/?name=John+Doe"
    },
    {
      id: 2,
      message: "Project deadline updated: Marketing Campaign",
      time: "5 hours ago",
      photo: "https://ui-avatars.com/api/?name=Jane+Smith"
    }
  ]);
  const [showProfile, setShowProfile] = useState(false);
  const [userPhoto, setUserPhoto] = useState(null);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [recentActivity] = useState([
    {
      id: 1,
      type: 'login',
      description: 'Logged in from Chrome on Windows',
      timestamp: '2024-03-20T10:30:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 2,
      type: 'password_change',
      description: 'Password changed successfully',
      timestamp: '2024-03-19T15:45:00',
      location: 'New York, USA',
      device: 'Chrome on Windows'
    },
    {
      id: 3,
      type: 'login',
      description: 'Logged in from Safari on iPhone',
      timestamp: '2024-03-18T09:15:00',
      location: 'New York, USA',
      device: 'Safari on iPhone'
    }
  ]);
  const [showSuccess, setShowSuccess] = useState(true);

  // Mock team members for demo
  const teamMembers = [
    {
      id: 1,
      name: 'Sanket Jadhav',
      email: 'sanket3280@gmail.com',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 2,
      name: 'Sanket tanaji jadhav',
      email: 'sankettanaj.p24@medhaviskil...',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 3,
      name: '25F1001327 SANKET JADHAV',
      email: '25f1001327@ds.study.iitm.ac...',
      status: 'Active',
      role: 'Member',
      avatar: '',
    },
    {
      id: 4,
      name: 'Sanket Jadhav',
      email: 'sj546400@gmail.com',
      status: 'Active',
      role: 'Member',
      avatar: 'https://ui-avatars.com/api/?name=Sanket+Jadhav&background=000000&color=fff',
    },
  ];

  const fetchProjects = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/projects", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setProjects(response.data);
    } catch (error) {
      console.error("Error fetching projects:", error);
      if (error.response?.status === 401) {
        setUser(null);
        navigate("/login", { replace: true });
      }
    }
  }, [navigate, setUser]);

  const fetchTasks = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/my-tasks", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  }, []);

  const fetchStats = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/tasks/stats", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      setStats(response.data);
    } catch (error) {
      console.error("Error fetching stats:", error);
    }
  }, []);

  useEffect(() => {
    fetchProjects();
    fetchTasks();
    fetchStats();
    // Load theme preference from localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch user profile photo
    const fetchUserPhoto = async () => {
      try {
        const response = await fetch('/api/users/profile', {
          credentials: 'include'
        });
        if (response.ok) {
          const data = await response.json();
          setUserPhoto(data.photo || null);
        }
      } catch (error) {
        console.error('Error fetching user photo:', error);
      }
    };

    fetchUserPhoto();
  }, [fetchProjects, fetchTasks, fetchStats]);

  const handleLogout = async () => {
    // Immediately clear user and redirect
    setUser(null);
    navigate("/login", { replace: true });

    // Then handle the server logout in the background
    try {
      await axios.get("http://localhost:5000/auth/logout", {
        withCredentials: true,
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
    setShowNotifications(false);
  };

  const handleProfileNavigation = () => {
    setShowProfile(false);
    navigate('/profile');
  };

  const handleNotificationsClick = () => {
    setShowNotifications(!showNotifications);
    setShowProfile(false);
  };

  const handleClickOutside = useCallback((event) => {
    if (showProfile && !event.target.closest('.profile-container')) {
      setShowProfile(false);
    }
    if (showNotifications && !event.target.closest('.notifications-container')) {
      setShowNotifications(false);
    }
  }, [showProfile, showNotifications]);

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [handleClickOutside]);

  return (
    <div className="dashboard">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-hidden">
        <Topbar
          user={user}
          userPhoto={userPhoto}
          notifications={notifications}
          onProfile={handleProfileNavigation}
          onLogout={handleLogout}
        />
        <main className="main-content">
          {/* Welcome Message */}
          <div className="mb-6">
            <h2 className="text-2xl font-bold">Welcome back, {user?.name}!</h2>
            <p className="text-gray-600">Here's what's happening in your projects today.</p>
          </div>

          {/* Stat Cards */}
          <section className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <div className="stat-card">
              <div className="stat-icon stat-projects">
                <i className="fas fa-folder"></i>
              </div>
              <div className="mt-2">
                <div className="text-gray-500">Projects</div>
                <div className="text-2xl font-bold">{projects.length}</div>
              </div>
            </div>
            <div className="stat-card">
              <div className="stat-icon stat-tasks">
                <i className="fas fa-tasks"></i>
              </div>
              <div className="mt-2">
                <div className="text-gray-500">Total Tasks</div>
                <div className="text-2xl font-bold">{stats.totalTasks}</div>
              </div>
            </div>
            <div className="stat-card">
              <div className="stat-icon stat-completed">
                <i className="fas fa-check-circle"></i>
              </div>
              <div className="mt-2">
                <div className="text-gray-500">Completed</div>
                <div className="text-2xl font-bold">{stats.completedTasks}</div>
              </div>
            </div>
            <div className="stat-card">
              <div className="stat-icon stat-overdue">
                <i className="fas fa-exclamation-circle"></i>
              </div>
              <div className="mt-2">
                <div className="text-gray-500">Overdue</div>
                <div className="text-2xl font-bold">{stats.overdueTasks}</div>
              </div>
            </div>
          </section>

          {/* Main Dashboard Sections */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <section className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold">My Tasks</h3>
                <button className="text-blue-500 hover:text-blue-600">View all</button>
              </div>
              <div className="space-y-4">
                {tasks.length === 0 ? (
                  <div className="empty-state">No tasks assigned to you</div>
                ) : (
                  tasks.map(task => (
                    <div key={task._id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                      <div>
                        <h4 className="font-medium">{task.title}</h4>
                        <p className="text-sm text-gray-500">{task.description}</p>
                      </div>
                      <span className={`px-2 py-1 rounded text-xs ${
                        task.status === 'completed' ? 'bg-green-100 text-green-800' :
                        task.status === 'in-progress' ? 'bg-blue-100 text-blue-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {task.status}
                      </span>
                    </div>
                  ))
                )}
              </div>
            </section>

            <section className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold">Recent Activity</h3>
                <button className="text-blue-500 hover:text-blue-600">View all</button>
              </div>
              <div className="space-y-4">
                {recentActivity.length === 0 ? (
                  <div className="empty-state">No recent activity</div>
                ) : (
                  recentActivity.map(activity => (
                    <div key={activity.id} className="flex items-start gap-4 p-4 bg-gray-50 rounded-lg">
                      <div className="w-8 h-8 rounded-full bg-blue-100 flex items-center justify-center">
                        <i className={`fas fa-${
                          activity.type === 'login' ? 'sign-in-alt' :
                          activity.type === 'password_change' ? 'key' :
                          'user'
                        }`}></i>
                      </div>
                      <div>
                        <p className="font-medium">{activity.description}</p>
                        <p className="text-sm text-gray-500">
                          {new Date(activity.timestamp).toLocaleString()}
                        </p>
                        <p className="text-xs text-gray-400">
                          {activity.location} â€¢ {activity.device}
                        </p>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </section>
          </div>

          {/* Bottom Sections */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-6">
            <section className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold">Recent Projects</h3>
                <button className="text-blue-500 hover:text-blue-600">View all</button>
              </div>
              <div className="space-y-4">
                {projects.length === 0 ? (
                  <div className="empty-state">No recent projects</div>
                ) : (
                  projects.map(project => (
                    <div key={project._id} className="p-4 bg-gray-50 rounded-lg">
                      <h4 className="font-medium">{project.title}</h4>
                      <p className="text-sm text-gray-500">{project.description}</p>
                    </div>
                  ))
                )}
              </div>
            </section>

            <section className="bg-white rounded-lg shadow-sm p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold">Team Members</h3>
                <button className="btn btn-primary">Add Team Member</button>
              </div>
              {showSuccess && (
                <div className="mb-4 p-4 bg-green-100 text-green-800 rounded-lg">
                  User deleted successfully!
                </div>
              )}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {teamMembers.map(member => (
                  <div key={member.id} className="flex items-center gap-4 p-4 bg-gray-50 rounded-lg">
                    <div className="w-12 h-12 rounded-full bg-blue-100 flex items-center justify-center">
                      {member.avatar ? (
                        <img src={member.avatar} alt={member.name} className="w-12 h-12 rounded-full" />
                      ) : (
                        <span className="text-xl font-semibold">{member.name[0]}</span>
                      )}
                    </div>
                    <div className="flex-1">
                      <h4 className="font-medium">{member.name}</h4>
                      <p className="text-sm text-gray-500">{member.email}</p>
                      <div className="flex gap-2 mt-2">
                        <span className="px-2 py-1 bg-green-100 text-green-800 rounded text-xs">
                          Active
                        </span>
                        <span className="px-2 py-1 bg-blue-100 text-blue-800 rounded text-xs">
                          Member
                        </span>
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button className="p-2 text-gray-500 hover:text-blue-500">
                        <FaUserEdit />
                      </button>
                      <button 
                        className="p-2 text-gray-500 hover:text-red-500"
                        onClick={() => setShowSuccess(true)}
                      >
                        <FaTrash />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </section>
          </div>
        </main>
      </div>
    </div>
  );
}

export default Dashboard;
