import { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/Profile.css';

function Profile({ user, setUser }) {
  const navigate = useNavigate();
  const [recentActivity, setRecentActivity] = useState([]);
  const [loginStats, setLoginStats] = useState({
    totalLogins: 0,
    lastLogin: '',
    activeSessions: 0,
    loginMethods: {
      email: 0,
      google: 0,
      github: 0
    }
  });
  const [userPhoto, setUserPhoto] = useState(null);
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [showSecurityModal, setShowSecurityModal] = useState(false);
  const [securitySettings, setSecuritySettings] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
    twoFactorEnabled: false
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeSessions, setActiveSessions] = useState([]);
  const [passwordHistory, setPasswordHistory] = useState([]);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }

    // Load theme preference
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setIsDarkTheme(savedTheme === 'dark');
      document.body.classList.toggle('dark-theme', savedTheme === 'dark');
    }

    // Fetch all user data
    fetchUserData();
  }, [user, navigate]);

  const fetchUserData = async () => {
    try {
      setLoading(true);
      const [profileRes, activityRes, statsRes, sessionsRes, historyRes] = await Promise.all([
        axios.get('/api/users/profile', { withCredentials: true }),
        axios.get('/api/users/activity', { withCredentials: true }),
        axios.get('/api/users/stats', { withCredentials: true }),
        axios.get('/api/users/sessions', { withCredentials: true }),
        axios.get('/api/users/password-history', { withCredentials: true })
      ]);

      if (profileRes.data.photo) {
        setUserPhoto(profileRes.data.photo);
      }
      setRecentActivity(activityRes.data);
      setLoginStats(statsRes.data);
      setActiveSessions(sessionsRes.data);
      setPasswordHistory(historyRes.data);
    } catch (error) {
      console.error('Error fetching user data:', error);
      setError('Failed to load user data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleThemeToggle = () => {
    setIsDarkTheme(!isDarkTheme);
    document.body.classList.toggle('dark-theme');
    localStorage.setItem('theme', isDarkTheme ? 'light' : 'dark');
  };

  const handleSecurityUpdate = async (e) => {
    e.preventDefault();
    if (securitySettings.newPassword !== securitySettings.confirmPassword) {
      setError("New passwords don't match");
      return;
    }

    try {
      const response = await axios.post('/api/users/security/update', securitySettings, {
        withCredentials: true
      });
      
      setShowSecurityModal(false);
      setSecuritySettings({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
        twoFactorEnabled: securitySettings.twoFactorEnabled
      });

      // Refresh user data
      fetchUserData();
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to update security settings');
    }
  };

  const handleLogoutSession = async (sessionId) => {
    try {
      await axios.post(`/api/users/sessions/${sessionId}/logout`, {}, {
        withCredentials: true
      });
      fetchUserData();
    } catch (error) {
      setError('Failed to logout session');
    }
  };

  const handleLogoutAllDevices = async () => {
    try {
      await axios.post('/api/auth/logout-all', {}, {
        withCredentials: true
      });
      fetchUserData();
    } catch (error) {
      setError('Failed to logout from all devices');
    }
  };

  if (loading) {
    return (
      <div className="profile-page loading">
        <div className="spinner"></div>
        <p>Loading profile data...</p>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-header">
        <div className="header-content">
          <div className="profile-info">
            {userPhoto ? (
              <img src={userPhoto} alt="Profile" className="profile-image" />
            ) : (
              <div className="profile-initial">
                {user?.name?.charAt(0) || user?.email?.charAt(0)}
              </div>
            )}
            <div className="user-details">
              <h1>{user?.name || 'User'}</h1>
              <p>{user?.email}</p>
              <span className="user-status">
                <i className="fas fa-circle"></i> Active Now
              </span>
            </div>
          </div>
          <div className="header-actions">
            <button 
              className="icon-button theme-toggle" 
              onClick={handleThemeToggle}
              title={isDarkTheme ? "Switch to Light Mode" : "Switch to Dark Mode"}
            >
              <i className={`fas fa-${isDarkTheme ? 'sun' : 'moon'}`}></i>
            </button>
            <Link to="/dashboard" className="back-btn" style={{ backgroundColor: 'var(--primary-color)', color: 'white' }}>
              <i className="fas fa-arrow-left" style={{ color: 'white' }}></i>
              Back to Dashboard
            </Link>
          </div>
        </div>
      </div>

      <div className="profile-content">
        {/* Security Section */}
        <div className="profile-section">
          <div className="section-header">
            <h2>Security</h2>
            <button className="security-btn" onClick={() => setShowSecurityModal(true)}>
              <i className="fas fa-shield-alt"></i>
              Security Settings
            </button>
          </div>
          <div className="security-overview">
            <div className="security-item">
              <i className="fas fa-lock"></i>
              <div className="security-info">
                <h3>Password Strength</h3>
                <div className="strength-meter">
                  <div className="strength-bar" style={{ width: '80%' }}></div>
                </div>
                <p>Last changed: {passwordHistory[0]?.date || 'Never'}</p>
              </div>
            </div>
            <div className="security-item">
              <i className="fas fa-mobile-alt"></i>
              <div className="security-info">
                <h3>Two-Factor Authentication</h3>
                <p>{securitySettings.twoFactorEnabled ? 'Enabled' : 'Disabled'}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Active Sessions Section */}
        <div className="profile-section">
          <div className="section-header">
            <h2>Active Sessions</h2>
            <button className="danger-btn" onClick={handleLogoutAllDevices}>
              <i className="fas fa-sign-out-alt"></i>
              Logout All Devices
            </button>
          </div>
          <div className="sessions-list">
            {activeSessions.map((session) => (
              <div key={session.id} className="session-item">
                <div className="session-info">
                  <i className={`fas fa-${session.deviceType === 'mobile' ? 'mobile-alt' : 'desktop'}`}></i>
                  <div>
                    <h4>{session.deviceName}</h4>
                    <p>{session.location} â€¢ {session.browser}</p>
                    <span className="session-time">Active since {new Date(session.lastActive).toLocaleString()}</span>
                  </div>
                </div>
                {!session.current && (
                  <button 
                    className="logout-session-btn"
                    onClick={() => handleLogoutSession(session.id)}
                  >
                    <i className="fas fa-times"></i>
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Recent Activity Section */}
        <div className="profile-section">
          <div className="section-header">
            <h2>Recent Activity</h2>
          </div>
          <div className="activity-list">
            {recentActivity.map((activity) => (
              <div key={activity.id} className="activity-item">
                <div className="activity-icon">
                  <i className={`fas fa-${activity.type === 'login' ? 'sign-in-alt' : 
                    activity.type === 'password_change' ? 'key' : 
                    activity.type === 'logout' ? 'sign-out-alt' : 'circle'}`}></i>
                </div>
                <div className="activity-content">
                  <p>{activity.description}</p>
                  <div className="activity-details">
                    <span><i className="fas fa-clock"></i> {new Date(activity.timestamp).toLocaleString()}</span>
                    <span><i className="fas fa-map-marker-alt"></i> {activity.location}</span>
                    <span><i className="fas fa-desktop"></i> {activity.device}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Login Statistics Section */}
        <div className="profile-section">
          <div className="section-header">
            <h2>Login Statistics</h2>
          </div>
          <div className="stats-grid">
            <div className="stat-card">
              <i className="fas fa-sign-in-alt"></i>
              <div className="stat-info">
                <h3>Total Logins</h3>
                <p>{loginStats.totalLogins}</p>
              </div>
            </div>
            <div className="stat-card">
              <i className="fas fa-clock"></i>
              <div className="stat-info">
                <h3>Last Login</h3>
                <p>{new Date(loginStats.lastLogin).toLocaleString()}</p>
              </div>
            </div>
            <div className="stat-card">
              <i className="fas fa-users"></i>
              <div className="stat-info">
                <h3>Active Sessions</h3>
                <p>{loginStats.activeSessions}</p>
              </div>
            </div>
          </div>
          <div className="login-methods">
            <h3>Login Methods</h3>
            <div className="method-bars">
              <div className="method-bar">
                <span>Email</span>
                <div className="bar-container">
                  <div className="bar" style={{ width: `${(loginStats.loginMethods.email / loginStats.totalLogins) * 100}%` }}></div>
                </div>
                <span>{loginStats.loginMethods.email}</span>
              </div>
              <div className="method-bar">
                <span>Google</span>
                <div className="bar-container">
                  <div className="bar" style={{ width: `${(loginStats.loginMethods.google / loginStats.totalLogins) * 100}%` }}></div>
                </div>
                <span>{loginStats.loginMethods.google}</span>
              </div>
              <div className="method-bar">
                <span>GitHub</span>
                <div className="bar-container">
                  <div className="bar" style={{ width: `${(loginStats.loginMethods.github / loginStats.totalLogins) * 100}%` }}></div>
                </div>
                <span>{loginStats.loginMethods.github}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showSecurityModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Security Settings</h2>
            <form onSubmit={handleSecurityUpdate}>
              <div className="security-section">
                <h3>Change Password</h3>
                <input
                  type="password"
                  placeholder="Current Password"
                  value={securitySettings.currentPassword}
                  onChange={(e) => setSecuritySettings({
                    ...securitySettings,
                    currentPassword: e.target.value
                  })}
                  required
                />
                <input
                  type="password"
                  placeholder="New Password"
                  value={securitySettings.newPassword}
                  onChange={(e) => setSecuritySettings({
                    ...securitySettings,
                    newPassword: e.target.value
                  })}
                  required
                />
                <input
                  type="password"
                  placeholder="Confirm New Password"
                  value={securitySettings.confirmPassword}
                  onChange={(e) => setSecuritySettings({
                    ...securitySettings,
                    confirmPassword: e.target.value
                  })}
                  required
                />
              </div>
              <div className="security-section">
                <h3>Two-Factor Authentication</h3>
                <label className="toggle-switch">
                  <input
                    type="checkbox"
                    checked={securitySettings.twoFactorEnabled}
                    onChange={(e) => setSecuritySettings({
                      ...securitySettings,
                      twoFactorEnabled: e.target.checked
                    })}
                  />
                  <span className="toggle-slider"></span>
                  Enable 2FA
                </label>
              </div>
              <div className="security-section">
                <h3>Password History</h3>
                <div className="password-history">
                  {passwordHistory.map((entry, index) => (
                    <div key={index} className="history-item">
                      <span>Changed on {new Date(entry.date).toLocaleString()}</span>
                      <span className="history-device">{entry.device}</span>
                    </div>
                  ))}
                </div>
              </div>
              <div className="modal-buttons">
                <button type="submit" className="submit-btn">
                  Save Changes
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setShowSecurityModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Profile;
